import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'QA';
  scriptOutput: string = '';
  inputText: string = '';

  constructor(private http: HttpClient) {}

  uploadFile(event: any) {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('file', file);

    this.http.post('http://localhost:5000/upload', formData).subscribe((data: any) => {
      this.scriptOutput = data;
    });
  }

  sendText() {
    this.http.post('http://localhost:5000/say_hi', { text: this.inputText }).subscribe((data: any) => {
      this.scriptOutput = data;
    });
  }
}
<div class="file-uploader">
  <h3>Tiny Title</h3>
  <input type="file" (change)="uploadFile($event)" />
  <div class="output-box">
    <pre>{{ scriptOutput }}</pre>
  </div>
  <input [(ngModel)]="inputText" placeholder="Type something..." />
  <button (click)="sendText()">Send Text</button>
</div>
