import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { OnInit } from '@angular/core';
import { initFlowbite } from 'flowbite';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})


export class AppComponent implements OnInit {
  title = 'QA';
  conversation : string[] = [];
  allowQuestion = true; 
  fileName: string = "all"; 
  fileContent: any = '';
  language: string = 'en'
  task: string = 'QA'

  // Database Files 
  allFiles: string[] = ["All"];
  languages = ['en', 'fr', 'pt', 'es'];
  tasks = ['QA', 'Translation'];

  // toast
  message = ''

  httpHeader = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json' 
    })
  };

  constructor(private http: HttpClient) {
    console.log('Start')
  }

    ngOnInit(): void {
    initFlowbite();
    // reset message
    setInterval(() => {this.message = '';}, 5000);
  }

  onFileSelected(fileList: FileList) {
    this.message = ''
    let file = fileList[0];
    let fileReader: FileReader = new FileReader();
    let text: any = '';
    let self = this;
    fileReader.onloadend = function (x) {
      // async
      text = fileReader.result;
      self.uploadFile(file.name, file.type, text);
    };
    fileReader.readAsText(file);
  }


  onLanguageChange(language: any) {
    if (language.value != this.language){
      // restore conversation on change 
      this.conversation = []
      this.language = language.value;
    }
  }
  onFileChange(fileName: any) {
    if (fileName.value != this.fileName){
      // restore conversation on change 
      this.conversation = []
      this.fileName = fileName.value;
    }
  }

  onTaskChange(task: any) {
    if (task.value != this.task){
      // restore conversation on change 
      this.conversation = []
      this.task = task.value;
    }
  }

  uploadFile(name: string, type:string, text: string) {
    this.http.post('http://localhost:5000/upload', {'name': name, 'type': type, 'text': text}, this.httpHeader).subscribe({
      next: (data: any) => {
        this.allFiles.push(name),
        console.log('data',data)
        this.message = 'data'
      },
      error: (error) => {
        console.log('Error upload', error),
        this.message = 'Error'
      }
    })
  }

  sendQuestion(text: string) {
    if (this.allowQuestion) {
      this.conversation.push(text);
      this.allowQuestion = false 
      this.http.post('http://localhost:5000/query', 
          {'text': text, "fileName": this.fileName, "language": this.language, "task": this.task}, 
          this.httpHeader).subscribe({
        next: (data: any) => {
          this.conversation.push(data.message) 
          this.allowQuestion = true;
          console.log('Received', data.message)
          this.message = 'data'
        },
        error: (error) => {
          const message_error = 'Message Error';
          this.conversation.push(message_error);
          this.allowQuestion = true;
          console.log('Error', error)
          this.message = 'Error'
        }
      })
    }
  }

}
<h1 class="big-title">Q&A with your Documents</h1>

<div class="center">
    <div class="message-container">
        <ng-container *ngFor="let message of conversation; let i = index">
            <div *ngIf="i % 2 == 0" class="flex items-start justify-start">
                <div class="text-blue-500 bg-white border border-gray-200 m-2 p-4 space-x-4 divide-x divide-gray-200 rounded-lg shadow dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800">
                    {{message}}
                </div>
            </div>
            <div *ngIf="i % 2 == 1" class="flex items-end justify-end">
                <div class=" text-black-500bg-white border border-gray-200 m-2 p-4 space-x-4 divide-x divide-gray-200 rounded-lg shadow dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800">
                    {{ message }}
                </div>
            </div>
        </ng-container>
    </div>

    <div class="w-full mb-4 border border-gray-200 rounded-lg bg-gray-50 dark:bg-gray-700 dark:border-gray-600">
        <div class="px-4 py-2 bg-white rounded-t-lg dark:bg-gray-800">
            <label for="question" class="sr-only">Make Question Label</label>
            <textarea id="question" rows="4" class="w-full px-0 text-sm text-gray-900 bg-white border-0 dark:bg-gray-800 focus:ring-0 dark:text-white dark:placeholder-gray-400" placeholder="Make a Question..." required
                #questionInput>
            </textarea>
        </div> 
        <div class="flex items-center px-5 py-5 border-t dark:border-gray-600">
            <div class="flex-auto space-x-4 mg inline-flex items-center flex-row">
                <button type="submit" class="inline-flex items-center py-2.5 px-4 text-xs font-medium text-center text-white bg-blue-700 rounded-lg focus:ring-4 focus:ring-blue-200 dark:focus:ring-blue-900 hover:bg-blue-800"
                (click)="sendQuestion(questionInput.value)">
                    Make Question
                </button>
                <div>
                    <label for="choseFile" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Chose File(s)</label>
                    <select id="choseFile" #selectedFile class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                            (change)="onFileChange($event)">
                        <option *ngFor="let fileName of allFiles" [value]="fileName">{{ fileName }} </option>
                    </select> 
                </div>
                <div>
                    <label for="language" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Chose Language</label>
                    <select id="language" #selectedLanguage  class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                            (change)="onLanguageChange($event)">
                        <option *ngFor="let language of languages" [value]="language">{{ language }} </option>
                    </select> 
                    <ng-container *ngFor="let language of languages">
                        <span class="absolute right-3 top-2 cursor-pointer text-red-500">X</span>
                    </ng-container>
                </div>
                <div>
                    <label for="task" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Chose Task</label>
                    <select id="task" #selectedLanguage  class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                            (change)="onTaskChange($event)">
                        <option *ngFor="let task of tasks" [value]="task">{{ task }} </option>
                    </select> 
                    <ng-container *ngFor="let language of languages">
                        <span class="absolute right-3 top-2 cursor-pointer text-red-500">X</span>
                    </ng-container>
                </div>
            </div>
            <div class="flex pl-0 space-x-1 sm:pl-2 ">
                <input
                    type="file"
                    id="fileInput"
                    accept=".pdf, .doc, .docx, .txt"
                    style="display: none;"
                    (change)="onFileSelected($event.target.files)"
                />
                <label for="fileInput" class="inline-flex justify-center items-center p-2 text-gray-500 rounded cursor-pointer hover:text-gray-900 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-white dark:hover:bg-gray-600">
                    <svg class="w-4 h-4" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 12 20">
                        <path stroke="currentColor" stroke-linejoin="round" stroke-width="2" d="M1 6v8a5 5 0 1 0 10 0V4.5a3.5 3.5 0 1 0-7 0V13a2 2 0 0 0 4 0V6"/>
                    </svg>
                    <span class="sr-only">Attach file</span>
                    Attach file
                </label>
            </div>
        </div>

    </div>
</div>
<div *ngIf='message' class="flex items-center w-full max-w-xs p-4 text-gray-500 bg-blue border-black border-4 rounded-lg shadow dark:text-gray-400 dark:bg-gray-800" role="alert">
    <div class="text-sm font-normal ">
       {{message}}
    </div>
</div>
