import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'QA';
  scriptOutput: string = '';
  inputText: string = '';

  constructor(private http: HttpClient) {
    console.log('Start')
  }

  uploadFile(event: any) {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('file', file);

    this.http.post('http://localhost:5000/upload', formData).subscribe((data: any) => {
      this.scriptOutput = data;
    });
    this.http.post('http://localhost:5000/upload', formData).subscribe({
      next: (data: any) => this.scriptOutput = data,
      error: (error) => this.scriptOutput ='Error uploading file'
    })
  }

  sendText(text: string) {
    this.http.post('http://localhost:5000/say_hi', { text: text }).subscribe({
      next: (data: any) => this.scriptOutput = data,
      error: (error) => this.scriptOutput ='Error uploading file'
    })
  }
}
<title>Q&A Chat with your docs</title>

<div class="output-box mb-4">
  <pre>{{ scriptOutput }}</pre>
</div>

<div class="w-full mb-4 border border-gray-200 rounded-lg bg-gray-50 dark:bg-gray-700 dark:border-gray-600">
  <div class="px-4 py-2 bg-white rounded-t-lg dark:bg-gray-800">
    <label for="comment" class="sr-only">Your Question</label>
    <textarea id="comment" rows="4" class="w-full px-0 text-sm text-gray-900 bg-white border-0 dark:bg-gray-800 focus:ring-0 dark:text-white dark:placeholder-gray-400" 
      placeholder="Make your question..." required></textarea>
  </div>
  <div class="flex items-center justify-between px-3 py-2 border-t dark:border-gray-600">
    <button type="button" class="inline-flex items-center py-2.5 px-4 text-xs font-medium text-center text-white bg-blue-700 rounded-lg focus:ring-4 focus:ring-blue-200 dark:focus:ring-blue-900 hover:bg-blue-800"
      ><!--(click)="sendText(inputRef.value)" -->
      Submit Question
    </button>
    <div class="flex pl-0 space-x-1 sm:pl-2">
      <button type="button" class="inline-flex justify-center items-center p-2 text-gray-500 rounded cursor-pointer hover:text-gray-900 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-white dark:hover:bg-gray-600"
        (click)="uploadFile($event)">
        <svg class="w-4 h-4" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 12 20">
          <path stroke="currentColor" stroke-linejoin="round" stroke-width="2" d="M1 6v8a5 5 0 1 0 10 0V4.5a3.5 3.5 0 1 0-7 0V13a2 2 0 0 0 4 0V6"/>
        </svg>
        <span class="sr-only">Upload file</span>
      </button>
    </div>
  </div>
</div>
