import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { OnInit } from '@angular/core';
import { initFlowbite } from 'flowbite';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'QA';
  conversation : string[] = [];
  allowQuestion = true; 

  // upload
  allFiles: string[] = [];
  //newFile: File | null;

  // user info 
  userId = 1;

  httpHeader = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json' 
    })
  };


  constructor(private http: HttpClient) {
    console.log('Start')
  }

    ngOnInit(): void {
    initFlowbite();
  }

  onFileSelected(event: any) {
    const newFile = event.target.files[0];
    console.log('New', newFile, event.target.files);
    this.uploadFile(newFile);
  }

  uploadFile(newFile: File) {
    this.http.post(`http://localhost:5000/upload/${this.userId}`, {'file': newFile}, this.httpHeader).subscribe({
      next: (data: any) => this.allFiles.push(newFile.name),
      error: (error) => console.log('Error upload'),
    })
  }

  sendQuestion(text: string, selectedFileName: string) {
    if (this.allowQuestion) {
      this.conversation.push(text);
      this.allowQuestion = false 
      this.http.post(`http://localhost:5000/send_message/${this.userId}`, {'text': text, "fileName": selectedFileName}, this.httpHeader).subscribe({
        next: (data: any) => {
          this.conversation.push(data.message) 
          this.allowQuestion = true;
          console.log('Received', data.message)
        },
        error: (error) => {
          const message_error = 'Message Error';
          this.conversation.push(message_error);
          this.allowQuestion = true;
          console.log('Error')
        }
      })
    }
  }

}
