import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { OnInit } from '@angular/core';
import { initFlowbite } from 'flowbite';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'QA';
  scriptOutput: string = '';
  inputText: string = '';

  constructor(private http: HttpClient) {
    console.log('Start')
  }

  ngOnInit(): void {
    initFlowbite();
  }

  uploadFile(event: any) {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('file', file);

    this.http.post('http://localhost:5000/upload', formData).subscribe((data: any) => {
      this.scriptOutput = data;
    });
    this.http.post('http://localhost:5000/upload', formData).subscribe({
      next: (data: any) => this.scriptOutput = data,
      error: (error) => this.scriptOutput ='Error uploading file'
    })
  }

  sendText(text: string) {
    this.http.post('http://localhost:5000/say_hi', { text: text }).subscribe({
      next: (data: any) => this.scriptOutput = data,
      error: (error) => this.scriptOutput ='Error uploading file'
    })
  }
}
