import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { OnInit } from '@angular/core';
import { initFlowbite } from 'flowbite';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'QA';
  selectedFile: File | null = null;
  scriptOutput: string = '';
  conversation : string[] = [];
  inputText: string = '';

  constructor(private http: HttpClient) {
    console.log('Start')
  }

    ngOnInit(): void {
    initFlowbite();
  }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile() {
    this.http.post('http://localhost:5000/upload', this.selectedFile).subscribe({
      next: (data: any) => this.scriptOutput = data,
      error: (error) => this.scriptOutput ='Error uploading file'
    })
  }

  sendText(text: string) {
    console.log('Sending')
    this.conversation.push(text);
    this.http.post('http://localhost:5000/send_message', { text: text }).subscribe({
      next: (data: any) => {
        this.conversation.push(data) 
        console.log('Received', data)
      },
      error: (error) => {
        const message_error = 'Message Error';
        this.conversation.push(message_error);
        console.log('Received', message_error);
      }
    })
  }
}
