import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { OnInit } from '@angular/core';
import { initFlowbite } from 'flowbite';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})


export class AppComponent implements OnInit {
  title = 'QA';
  conversation : string[] = [];
  allowQuestion = true; 
  selectedFileName: string = "all"; 
  language: string = 'en'

  // Database Files 
  allFiles: string[] = ["All"];
  languages = ['en', 'fr', 'pt', 'es'];

  httpHeader = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json' 
    })
  };

  constructor(private http: HttpClient) {
    console.log('Start')
  }

    ngOnInit(): void {
    initFlowbite();
  }

  onFileSelected(event: any) {
    const newFile = event.target.files[0];
    console.log('New', newFile, event.target.files);
    this.uploadFile(newFile);
  }

  uploadFile(newFile: File) {
    console.log('File', newFile)
    this.http.post('http://localhost:5000/upload', {'file': newFile}, this.httpHeader).subscribe({
      next: (data: any) => this.allFiles.push(newFile.name),
      error: (error) => console.log('Error upload'),
    })
  }

  sendQuestion(text: string, selectedFile: string) {
    if (this.allowQuestion) {
      this.conversation.push(text);
      this.allowQuestion = false 
      this.http.post('http://localhost:5000/query', 
          {'text': text, "fileName": this.selectedFileName, "language": this.language}, 
          this.httpHeader).subscribe({
        next: (data: any) => {
          this.conversation.push(data.message) 
          this.allowQuestion = true;
          console.log('Received', data.message)
        },
        error: (error) => {
          const message_error = 'Message Error';
          this.conversation.push(message_error);
          this.allowQuestion = true;
          console.log('Error')
        }
      })
    }
  }

}
<h1 class="big-title">Q&A with your Documents</h1>
<div class="center">
    <div class="message-container">
        <ng-container *ngFor="let message of conversation; let i = index">
            <div *ngIf="i % 2 == 0" class="flex items-start justify-start">
                <div class="text-blue-500 bg-white border border-gray-200 m-2 p-4 space-x-4 divide-x divide-gray-200 rounded-lg shadow dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800">
                    {{message}}
                </div>
            </div>
            <div *ngIf="i % 2 == 1" class="flex items-end justify-end">
                <div class=" text-black-500bg-white border border-gray-200 m-2 p-4 space-x-4 divide-x divide-gray-200 rounded-lg shadow dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800">
                    {{ message }}
                </div>
            </div>
        </ng-container>
    </div>

    <div class="w-full mb-4 border border-gray-200 rounded-lg bg-gray-50 dark:bg-gray-700 dark:border-gray-600">
        <div class="px-4 py-2 bg-white rounded-t-lg dark:bg-gray-800">
            <label for="question" class="sr-only">Make Question Label</label>
            <textarea id="question" rows="4" class="w-full px-0 text-sm text-gray-900 bg-white border-0 dark:bg-gray-800 focus:ring-0 dark:text-white dark:placeholder-gray-400" placeholder="Make a Question..." required
                #questionInput>
            </textarea>
        </div>
        <div class="flex items-center justify-between px-3 py-2 border-t dark:border-gray-600">
            <div class="inline-flex items-center py-2.5 px-10 flex-row">
                <button type="submit" class="inline-flex items-center py-2.5 px-4 text-xs font-medium text-center text-white bg-blue-700 rounded-lg focus:ring-4 focus:ring-blue-200 dark:focus:ring-blue-900 hover:bg-blue-800"
                (click)="sendQuestion(questionInput.value, selectedFile.value)">
                    Make Question
                </button>
                <div>
                    <label for="choseFile" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Select file(s) for QA</label>
                    <select id="small" #selectedFile class="tiny-box block w-full p-2 mb-6 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
                        <option *ngFor="let fileName of allFiles" [value]="fileName">{{ fileName }} </option>
                    </select>
                    <div>
                        <div class="relative inline-block text-left">
                            <select type="button" class="inline-flex w-full justify-center gap-x-1.5 rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50" id="menu-button" aria-expanded="true" aria-haspopup="true">
                                Chose File
                                <svg class="-mr-1 h-5 w-5 text-gray-400" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                    <path fill-rule="evenodd" d="M5.23 7.21a.75.75 0 011.06.02L10 11.168l3.71-3.938a.75.75 0 111.08 1.04l-4.25 4.5a.75.75 0 01-1.08 0l-4.25-4.5a.75.75 0 01.02-1.06z" clip-rule="evenodd" />
                                </svg>
                                <option *ngFor="let fileName of allFiles" [value]="fileName">{{ fileName }} </option>
                            </select>
                            <div class="absolute right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none" role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabindex="-1">
                                <div class="py-1" role="none">
                                    <ng-container *ngFor="let fileName of allFiles; let i = index">
                                        <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-{{i+1}}">{{ fileName }}</a>
                                    </ng-container>

                                    <!-- Example form with dynamic option -->
                                    <form method="POST" action="#" role="none">
                                        <button type="submit" class="text-gray-700 block w-full px-4 py-2 text-left text-sm" role="menuitem" tabindex="-1" id="menu-item-3">Sign out</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                        <div class="relative inline-block text-left">
                            <div>
                                <button type="button" class="inline-flex w-full justify-center gap-x-1.5 rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50" id="menu-button" aria-expanded="true" aria-haspopup="true">
                                    Chose Language
                                    <svg class="-mr-1 h-5 w-5 text-gray-400" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                        <path fill-rule="evenodd" d="M5.23 7.21a.75.75 0 011.06.02L10 11.168l3.71-3.938a.75.75 0 111.08 1.04l-4.25 4.5a.75.75 0 01-1.08 0l-4.25-4.5a.75.75 0 01.02-1.06z" clip-rule="evenodd" />
                                    </svg>
                                </button>
                            </div>
                            <div class="absolute right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none" role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabindex="-1">
                                <div class="py-1" role="none">
                                    <ng-container *ngFor="let language of languages; let i = index">
                                        <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-{{i+1}}">{{ language }}</a>
                                    </ng-container>

                                    <!-- Example form with dynamic option -->
                                    <form method="POST" action="#" role="none">
                                        <button type="submit" class="text-gray-700 block w-full px-4 py-2 text-left text-sm" role="menuitem" tabindex="-1" id="menu-item-3">Sign out</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="relative inline-block text-left">
                        <div>
                            <select class="inline-flex w-full justify-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50" 
                                    [(ngModel)]="selectedLanguage" 
                                    (change)="updateLanguage()">
                                <option disabled selected>Choose Language</option>
                                <ng-container *ngFor="let language of languages; let i = index">
                                    <option [value]="language" id="menu-item-{{i+1}}">{{ language }}</option>
                                </ng-container>
                            </select>
                        </div>
                    </div>

                </div>

            </div>
            <div class="flex pl-0 space-x-1 sm:pl-2">
                <input
                    type="file"
                    id="fileInput"
                    accept=".pdf, .doc, .docx, .txt"
                    style="display: none;"
                    (change)="onFileSelected($event)"
                />
                <label for="fileInput" class="inline-flex justify-center items-center p-2 text-gray-500 rounded cursor-pointer hover:text-gray-900 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-white dark:hover:bg-gray-600">
                    <svg class="w-4 h-4" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 12 20">
                        <path stroke="currentColor" stroke-linejoin="round" stroke-width="2" d="M1 6v8a5 5 0 1 0 10 0V4.5a3.5 3.5 0 1 0-7 0V13a2 2 0 0 0 4 0V6"/>
                    </svg>
                    <span class="sr-only">Attach file</span>
                    Attach file
                </label>
            </div>

        </div>
    </div>


</div>
