import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'QA';
  scriptOutput: string = '';
  inputText: string = '';

  constructor(private http: HttpClient) {
    console.log('Start')
  }

  uploadFile(event: any) {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('file', file);

    this.http.post('http://localhost:5000/upload', formData).subscribe((data: any) => {
      this.scriptOutput = data;
    });
    this.http.post('http://localhost:5000/upload', formData).subscribe({
      next: (data: any) => this.scriptOutput = data,
      error: (error) => this.scriptOutput ='Error uploading file'
    })
  }

  sendText(text: string) {
    this.http.post('http://localhost:5000/say_hi', { text: text }).subscribe({
      next: (data: any) => this.scriptOutput = data,
      error: (error) => this.scriptOutput ='Error uploading file'
    })
  }
}
<div class="text-center">
  <h2 class="text-2xl font-semibold mb-4">Q&A Chat with your docs</h2>
</div>

<div class="flex justify-center items-center">
  <!-- Upload Button -->
  <div class="file-uploader mr-4">
    <label>Upload Your File Here</label>
    <input type="file" (change)="uploadFile($event)" />
  </div>

  <!-- Output Box and Text Input -->
  <div>
    <div class="output-box mb-4">
      <pre>{{ scriptOutput }}</pre>
    </div>
    <input #inputRef class="w-full border border-gray-300 rounded p-2" placeholder="Type something..." />
    <button (click)="sendText(inputRef.value)" class="bg-blue-500 text-white px-4 py-2 rounded mt-2">Send Text</button>
  </div>
</div>
